mapper.select:
select
from t_pd_pad where 1=1
AND C_Mac = #{padMac}

SELECT I_PadId,C_Mac,SUBSTRING(C_Tags,1,LENGTH(C_Tags)-1) C_Tags,NOW() T_CTime, NOW() T_UTime FROM( SELECT a.I_PadId, a.C_Mac C_Mac ,CONCAT( IF(IFNULL(a.I_SchoolId,'')='','',CONCAT(a.I_SchoolId,',')) ,IF(IFNULL(b.I_XGradeId,'')='','',CONCAT(b.I_XGradeId,',')) ,IF(IFNULL(b.I_GradeId,'')='','',CONCAT(b.I_GradeId,',')) ,IF(IFNULL(a.I_ClassId,'')='','',CONCAT(a.I_ClassId,',')) ,IF(IFNULL(a.I_UserId,'')='','',CONCAT(a.I_UserId,',')) ,IF(IFNULL(a.I_PadId,'')='','',CONCAT(a.I_PadId,',')) ) C_Tags FROM t_pd_pad a LEFT JOIN t_xu_class b ON a.I_ClassId=b.I_ClassId WHERE 1=1
AND a.I_PadId=#{padId}
)t

SELECT

FROM t_pd_pad
WHERE 1=1

    AND I_SchoolId = #{schoolIds}


    AND FIND_IN_SET(I_ClassId,'${classId}')


    AND C_Mac = #{padMac}


    AND I_FactoryId =#{factoryId}


    AND I_DeviceId =#{deviceId}


    AND C_Model =#{model}


    AND C_FVersion =#{fVersion}


    AND I_Type =#{type}


    AND I_Status =#{status}

    ORDER BY T_FUTime DESC

    LIMIT #{pNum},#{pLine}

select COUNT(I_PadId) countAll from t_pd_pad
WHERE 1=1

    AND I_SchoolId = #{schoolIds}


    AND FIND_IN_SET(I_ClassId,'${classId}')


    AND C_Mac = #{padMac}


    AND I_FactoryId =#{factoryId}


    AND I_DeviceId =#{deviceId}


    AND C_Model =#{model}


    AND C_FVersion =#{fVersion}


    AND I_Type =#{type}


    AND I_Status =#{status}
